mapper.select:
select count(*) as fnum,
faculty_name,faculty_code,batch_year,school_code
from enroll_student


        and school_code = #{schoolCode,jdbcType=VARCHAR}


        and batch_year = #{batchYear,jdbcType=INTEGER}


        and enroll__status = #{enrollStatus,jdbcType=VARCHAR}

 GROUP BY faculty_code

select count(*) as fnum,
specialty_code,specialty_name,faculty_code,batch_year,school_code
from enroll_student


        and school_code = #{schoolCode,jdbcType=VARCHAR}


        and batch_year = #{batchYear,jdbcType=INTEGER}


        and faculty_code = #{facultyCode,jdbcType=VARCHAR}


        and enroll__status = #{enrollStatus,jdbcType=VARCHAR}

 GROUP BY specialty_code

select count(*) as fnum,
enrollteacher_id,teacher_name,batch_year,school_code
from enroll_student


        and school_code = #{schoolCode,jdbcType=VARCHAR}


        and batch_year = #{batchYear,jdbcType=INTEGER}


        and batch_id = #{batchId,jdbcType=INTEGER}


        and enroll__status = #{enrollStatus,jdbcType=VARCHAR}

 GROUP BY enrollteacher_id

select count(*) as fnum,
fromplace,batch_year,school_code
from enroll_student


        and school_code = #{schoolCode,jdbcType=VARCHAR}


        and batch_year = #{batchYear,jdbcType=INTEGER}


        and enroll__status = #{enrollStatus,jdbcType=VARCHAR}

 GROUP BY fromplace

select count(sex) as bmnum,
batch_id,batch_name,enroll__status,school_code,batch_year
from enroll_student


        and school_code = #{schoolCode,jdbcType=VARCHAR}


        and batch_year = #{batchYear,jdbcType=INTEGER}


        and sex = #{sex,jdbcType=VARCHAR}

 GROUP BY  batch_id

select count(sex) as rnum,
batch_id,enroll__status,batch_year,school_code
from enroll_student


        and school_code = #{schoolCode,jdbcType=VARCHAR}


        and batch_year = #{batchYear,jdbcType=INTEGER}


        and batch_id = #{batchId,jdbcType=INTEGER}


        and sex = #{sex,jdbcType=VARCHAR}


        and enroll__status = #{enrollStatus,jdbcType=VARCHAR}
